<?php #//php_start\\;		/**	* Action_Base is the form action request fields class	* 	*/		class Action_Base	{				public $params = array();				/**		* Check if ajax request		*/		public function is_ajax()		{			if(!empty($_SERVER['HTTP_X_REQUESTED_WITH']) && strtolower($_SERVER['HTTP_X_REQUESTED_WITH']) == 'xmlhttprequest')				return true;		}				/**		* Assigner of the fields to params with value		*/		public function __construct($opt = "")		{			if($opt == "POST"){				foreach($_POST as $fldname => $value)				{					$this->params[$fldname] = $value;				}								return $_POST;							}else if($opt == "GET"){				foreach($_GET as $fldname => $value)				{					$this->params[$fldname] = $value;				}				return $_GET;			}else if($opt == "FILES"){				foreach($_FILES as $fldname => $value)				{					$this->params[$fldname] = $value;				}				return $_FILES;			}else if($opt == "SERVER"){				foreach($_SERVER as $fldname => $value)				{					$this->params[$fldname] = $value;				}								return $_SERVER;			}		}				/**		* Parameter value server		*/		public function param($name = "")		{			if(isset($this->params[$name])) return $this->params[$name];		}				/**		* for post type		*/ 		public function POST()		{			return new Action_Base('POST');		}				/**		* for get type		*/		public function GET()		{			return new Action_Base('GET');		}				/*		* for files type		*/		public function FILES()		{			return new Action_Base('FILES');		}			}	#//php_end\\;?>