<?php #//php_start\\;		/**	* Action_Base is the form action request fields manager	* 	*/		class Action_Base	{				var $params = array();				/**		* Assigner of the fields to params with value		*/		public function __construct($opt = "")		{			if($opt == "POST"){				foreach($_POST as $fldname => $value)				{					$this->params[$fldname] = $value;				}			}else if($opt == "GET"){				foreach($_GET as $fldname => $value)				{					$this->params[$fldname] = $value;				}			}else if($opt == "FILES"){				foreach($_FILES as $fldname => $value)				{					$this->params[$fldname] = $value;				}			}else if($opt == "SERVER"){				foreach($_SERVER as $fldname => $value)				{					$this->params[$fldname] = $value;				}			}		}				/**		* Parameter value server		*/		public function param($name = "")		{			if(isset($this->params[$name])) return $this->params[$name];		}				/**		* for post type		*/ 		public function POST()		{			return new Action_Base('POST');		}				/**		* for get type		*/		public function GET()		{			return new Action_Base('GET');		}				/*		* for files type		*/		public function FILES()		{			return new Action_Base('FILES');		}			}	#//php_end\\;?>