<?php #//php_start\\;			class _CORE	{		private $event_handler = null;				public function __construct(){			$this->read_system();		}		/**		* read the system		*/		public function read_system()		{			$this->event_handler = new _SYSTEM();		}		/**		* execute the application		*/		public function exec_app()		{			return $this->event_handler->application();		}	}	#//php_end\\;?>