<?php #//php_start\\;			require_once('db.c.queries.controller.php');		class _QUERY	{		protected $connection;		public $db_prefix = null;		public function __construct($dbconn, $db_prefix = '')		{			$this->connection = $dbconn;						$this->db_prefix = $db_prefix;					}				/**		* A query		*/		public function query($qstmt)		{			return new _QUERY_CONTROLLER($qstmt, $this->connection, $this->db_prefix);		}				/**		* Ready easy insert method		*/		public function insert($table_name)		{			$qstmt = "INSERT INTO " . $this->db_prefix . $table_name . " ";						return new _QUERY_CONTROLLER($qstmt, $this->connection, $this->db_prefix);		}							/**		* Escape string on time of inserting		* data into database.		*/		public function escape_string($value)		{			if (get_magic_quotes_gpc()) {				$value = stripslashes($value);			}			return mysql_real_escape_string($value, $this->connection);		}				/**		* Ready easy select query method		*/		public function select($flds = "")		{			$get_fields = is_array($flds) ? implode(',', $flds) : "*";						$qstmt = "SELECT " . $get_fields . " ";						return new _QUERY_CONTROLLER($qstmt, $this->connection, $this->db_prefix);					}				/**		* Delete sql method		*/		public function delete($table_name)		{			$qstmt = "DELETE FROM " . $this->db_prefix . $table_name . " ";						return new _QUERY_CONTROLLER($qstmt, $this->connection, $this->db_prefix);		}						/**		* Ready easy update method		*/		public function update($table_name)		{			$qstmt = "UPDATE " . $this->db_prefix . $table_name . " ";			return new _QUERY_CONTROLLER($qstmt, $this->connection, $this->db_prefix);		}	}	#//php_end\\;?>