<?php #//php_start\\;			require_once('db.c.queries.controller.php');		class _QUERY	{		protected $connection;				public function __construct($dbconn)		{			$this->connection = $dbconn;		}				/**		* A query		*/		public function query($qstmt)		{			return new _QUERY_CONTROLLER($qstmt, $this->connection);		}				/**		* Ready easy insert method		*/		public function insert($table_name)		{			$qstmt = "INSERT INTO " . $table_name . " ";						return new _QUERY_CONTROLLER($qstmt, $this->connection);		}							/**		* Escape string on time of inserting		* data into database.		*/		public function escape_string($value)		{			if (get_magic_quotes_gpc()) {				$value = stripslashes($value);			}			return mysql_real_escape_string($value, $this->connection);		}				/**		* Ready easy select query method		*/		public function select($flds = "")		{			$get_fields = is_array($flds) ? implode(',', $flds) : "*";						$qstmt = "SELECT " . $get_fields . " ";						return new _QUERY_CONTROLLER($qstmt, $this->connection);					}				/**		* Delete sql method		*/		public function delete($table_name)		{			$qstmt = "DELETE FROM " . $table_name . " ";						return new _QUERY_CONTROLLER($qstmt, $this->connection);		}						/**		* Ready easy update method		*/		public function update($table_name)		{			$qstmt = "UPDATE " . $table_name . " ";			return new _QUERY_CONTROLLER($qstmt, $this->connection);		}	}	#//php_end\\;?>